// NOTE: this class should be auto-generated by the new code gen
var Class = require("../../structures/class");
var BaseGame = require("../baseGame");
var extend = require("extend");

var GeneratedGame = Class(BaseGame, {
	init: function() {
		BaseGame.init.apply(this, arguments);

		this.name = "checkers";

		extend(this.gamelog, {
			gameName: "checkers",
		});

		this._validCommands.push(
			"move",
			"done"
		);
	},

	// The following functions will need to be inherited in the main Game() class for checkers and implimented. There is no need to call these functions as the super, they are  here to help guide development and give something to run for the server.

	move: function(player, data) {
		// Inherit this function in the main game class and impliment it
		return false;
	},

	done: function(player, data) {
		// Inherit this function in the main game class and impliment it
		return false;
	},
});

module.exports = GeneratedGame;
